  executable?: boolean;
  // Create parent directories
  createDirs?: boolean;
  // Overwrite existing
  overwrite?: boolean;
}

async function writeFile(
  path: string,
  content: string,
  options?: WriteOptions
): Promise<void>;

// Write multiple files atomically
async function writeFiles(
  files: Array<{ path: string; content: string; options?: WriteOptions }>
): Promise<void>;
```

## Implementation Notes
- Use Bun.write for file writing
- chmod +x for executable files (pre-commit hook, bin)
- Create parent directories automatically
- Consider atomic writes (write to temp, then rename)

